import React, { useState } from "react";
import "./navi.css";

export default function Navi() {
  const [Click, setClick] = useState(false);

  const handleclick = () => {
    setClick(!Click);
  };

  const naviList = [
    ["Hakkımızda", "#about"],
    ["Hizmetler", "#services"],
    ["Fiyatlar", "#prices"],
    ["İletişim", "#contact"],
  ].map(([list, url]) => (
    <li>
      <a className="list-Link" href={url}>
        {" "}
        {list}
      </a>
    </li>
  ));

  return (
    <header className="main-nav">
      <div className="line"></div>
      <div className="logo">
        <h1>
          HTC Güzellik <br /> Salonu
        </h1>
      </div>
      <ul className="list">{naviList}</ul>
      <div className="toggle-button" onClick={handleclick} >
        <span className="bar"></span>
        <span className="bar"></span>
        <span className="bar"></span>
      </div>
     <section className ="mobile">
     <div className="logo">
        <h1>
          HTC Güzellik <br /> Salonu
        </h1>
      </div>
     
     
        <ul className= {Click? "mobile ul":"close" }>
           <hr className="line" />
         {naviList}
      </ul>
     
    </section>
    </header>
  );
}
