import { click } from '@testing-library/user-event/dist/click';
import { list } from 'postcss';
import React from 'react'
import { Link } from "react-router-dom";

export default function Navi() {
 
    const naviList = ["Hakkımızda" ,"Hizmetler", "İletişim", "Fiyat Listesi"].map((list)=> 
   <li className='float-left list-none pr-4 text-white hover: text-3xl' click={clickMenuItemControl(this.name)}>{list}</li>
    ) 
   function clickMenuItemControl(name){
  naviList[0] ===name
? <Link to="/Hakkimda"/> : null
  naviList[1] ===name
  ? "/Hizmetler" : null
  naviList[2] === name
  ? "/Iletişim" : null
    }
  return (
    <div className='bg-slate-600 h-24'>
    <div className='bg-slate-700 w-40 h-24 -mt-6 shadow-lg shadow-slate-400'  >
        <h1 className='text-white font-bold text-3xl float-left pl-12 pt-8 '>LOGO</h1>
    </div>
    <ul className='float-right -mt-12'>
      {naviList}
    </ul>
    </div>
  )
}
