import React from "react";
import "./mainMenu.css";
import images from "../../img/image";
import { ServicesPage } from "../../Pages";
import { AiOutlineArrowLeft, AiOutlineArrowRight } from "react-icons/ai";

const sideleft = () => {
  var sileder = document.getElementById("slider");
  sileder.scrollTop = sileder.scrollTop - 800;
};

const sideright = () => {
  var sileder = document.getElementById("slider");
    sileder.scrollLeft = sileder.scrollTop + 800;
};

const imagelist = [images.sliderImg1,images.sliderImg2,images.sliderImg3,images.sliderImg4].map((image) => 
  <li>
   <div>
   <img className="w-full h-full" src={image} alt="/" />
   </div>
  </li>
);
export default function MainMenu() {
  return (
    <main>
      <section className="image-slidebar">
        <AiOutlineArrowLeft onClick={sideleft} className="arrow float-left" />
        <div className="slider">
          <ul className="image">{imagelist}</ul>
        </div>
        <AiOutlineArrowRight onClick={sideright} className="arrow float-right" />
      </section>
    </main>
  );
}
